//this is the clock class that is imported into App.js and rendered in the App function 

import React from 'react';
import './App.css';

class Clock extends React.Component {
    constructor(props) {
        super(props);
        this.state = {date: new Date()};
    }
    //This is a lifecycle method that is called after a component is rendered to the DOM. It sets the state with the current time and date. 
    //It also sets the state to update every second.
    componentDidMount() {
        this.timerID = setInterval(
            () => this.tick(),
            1000
        );
    }
    //This is a lifecycle method that is called right before a component is removed from the DOM. It clears the timer.
    componentWillUnmount() {
        clearInterval(this.timerID);
    }
    //This is a lifecycle method that is called after the state is updated. It sets the state with the current time and date.
    tick() {
        this.setState({
            date: new Date()
        });
    }
    //This is a lifecycle method that is called after the state is updated. It renders the clock. 
    //It creates a div with a class of clock. The div contains a div with a class of clock-face. The clock-face div contains 3 divs with classes of clock-hand, clock-hour-hand, and clock-minute-hand. 
    //The clock-hand div is rotated by the hour and minute hands depending on the current time.
    render() {
        return (
            <div className="clock">
                <div className="clock-face">
                    <div className="clock-hand clock-hour-hand"></div>
                    <div className="clock-hand clock-minute-hand"></div>
                </div>
            </div>
        );
    }
}

export default Clock;
