//this is the calulator component that is rendered in App.js. This component will be a simple calculator that can add, subtract, multiply, and divide.
//this component will give a user the ability to input numbers and operators and display the result of the calculation.
//this component will also give the user the ability to clear the calculator and start over.
//this component will also give the user the ability to change the sign of the number that is currently displayed.
//this component will also give the user the ability to change the number that is currently displayed to a percentage.
//this component will also give the user the ability to change the number that is currently displayed to a decimal.
//this component will also give the user the ability to change the number that is currently displayed to a whole number.
//this component will also give the user the ability to change the number that is currently displayed to a fraction.
//this component will also give the user the ability to change the number that is currently displayed to a square root.

class Calculator extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            value: null,
            displayValue: '0',
            waitingForOperand: false,
            operator: null,
        };
    }

    //This is a lifecycle method that is called after the state is updated. It renders the calculator. 
    //It creates a div with a class of calculator. The div contains a div with a class of calculator-display. The calculator-display div contains a div with a class of calculator-display-value. 
    //The calculator-display div contains a div with a class of calculator-display-value. The calculator-display-value div contains the current value of the calculator.
    //The calculator div contains 4 divs with classes of calculator-keypad. The calculator-keypad divs contain 4 divs with classes of input-keys. The input-keys divs contain 4 divs with classes of function-keys. 
    //The function-keys divs contain 4 divs with classes of operator-keys. The operator-keys divs contain 4 divs with classes of number-keys. The number-keys divs contain 4 divs with classes of decimal-key. 
    //The decimal-key div contains a div with a class of clear-key. The clear-key div contains a div with a class of equals-key. The equals-key div contains a div with a class of zero-key


    render() {
        const { displayValue } = this.state;

        return (
            <div className="calculator">
                <div className="calculator-display">
                    <div className="calculator-display-value">{displayValue}</div>
                </div>
                <div className="calculator-keypad">
                    <div className="input-keys">
                        <div className="function-keys">
                            <div className="operator-keys">
                                <div className="number-keys">
                                    <div className="decimal-key">
                                        <div className="clear-key">
                                            <div className="equals-key">
                                                <div className="zero-key">
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Calculator;
